class Accordionify{constructor(u,d){this.u=document.querySelector(u);this.d=d;d.forEach(e=>{this.l=document.createElement('li');this.u.appendChild(this.l);this.b=document.createElement('button');this.b.innerHTML=e.btnText+'<span>+</span>';this.b.addEventListener('click',this.sHP.bind(this));this.l.appendChild(this.b);this.dv=document.createElement('div');this.dv.innerHTML=e.divHTML;this.l.appendChild(this.dv);});}sHP(e){e.target.classList.toggle("btn-hilite");const m=e.target.nextElementSibling;m.style.height=m.style.height?null:m.scrollHeight+"px";};}